import TheAside from "./components/TheAside";
import TheHeader from "./components/TheHeader";
import TheBody from "./components/TheBody";
import randomData from "./js/random";
export default {
  name: 'App',
  components: {
    TheAside,
    TheHeader,
    TheBody
  },
  beforeMount() {
    let today, mon, total, lasttoday, lastmon, lasttotal;
    this.$store.state.goods.brandData = randomData();
    let date = this.$store.state.goods.brandData;
    for (let i in date) {
      today += date[i].today;
      mon += date[i].mon;
      total += date[i].total;
      lasttoday += date[i].lasttoday;
      lastmon += date[i].lastmon;
      lasttotal += date[i].lasttotal;
    }
    console.log(today, mon, total, lasttoday, lastmon, lasttotal);
  }
};
