import Vue from 'vue'
import App from './App.vue'
import ElementUI from 'element-ui';
import 'element-ui/lib/theme-chalk/index.css';
import VueRouter from 'vue-router'
import Vuex from 'vuex'
import store from "@/store/store";
import TheBody from "@/components/TheBody";
import TheGoods from "@/components/body/TheGoods";
import TheUser from "@/components/body/TheUser";

Vue.use(Vuex)
Vue.use(ElementUI);
Vue.use(VueRouter);


Vue.config.productionTip = false
// const store=new Vuex.Store({})
// console.log(store.state)

const routes = [
  { path: '/home', component: TheBody },
  { path: '/goods', component: TheGoods },
  { path: '/uer', component: TheUser },
  { path: '/goods', component: TheGoods },
  { path: '/goods', component: TheGoods },
]

const router = new VueRouter({
  routes // (缩写) 相当于 routes: routes
})

new Vue({
  render: h => h(App),
  store,
  router,
}).$mount('#app')
