import * as echarts from'echarts'

this.$store

var getCanvas=function (lineData,dom,xAxis) {
    const  echarts1=echarts.init(dom)
    return echarts1.setOption({
        title: {
            text: '近半年数据对比',
            left:'left'
        },
        tooltip: {
            trigger: 'axis'
        },
        legend: {
            right:20,
            orient: 'vertical',
        },
        grid: {
            left: '3%',
            right: '4%',
            bottom: '3%',
            containLabel: true
        },
        toolbox: {
            feature: {
                saveAsImage: {}
            }
        },
        xAxis: {
            type: 'category',
            data: xAxis
        },
        yAxis: {
            type: 'value'
        },
        series: lineData

    });
}
export default getCanvas
