import "core-js/modules/es.array.push.js";
import * as echarts from 'echarts';
export default {
  name: "RightMiddle",
  mounted() {
    let date = this.$store.state.goods.brandData;
    let lineData = [];
    console.log(date);
    date.forEach((e, index) => {
      console.log(e.monthData[0]);
      let brand = [];
      for (let i = 6; i > 0; i--) {
        let mon = new Date().getMonth() - i + 2;
        brand.push(e.monthData[0]['month' + mon]);
      }
      lineData.push({
        data: brand,
        name: e.brandName,
        type: 'line',
        stack: 'x'
      });
    });
    console.log(lineData);
    const echarts1 = echarts.init(this.$refs.lineChart);
    echarts1.setOption({
      data: ["1", "2", "3"],
      xAxis: {
        type: 'category',
        data: ['五月', '六月', '七月', '八月', '九月', '十月']
      },
      yAxis: {
        type: 'value'
      },
      series: lineData
    });
  }
};
