import * as echarts from'echarts'


var getCanvas=function (echaetsType,brandData,dom,chooseMonth) {
    const  echarts1=echarts.init(dom)
    let canvas
    let xAxis=[]
    let title=""
    if(echaetsType=="line"){
        let lineData=[]
        brandData.forEach((e)=>{
            let chooseData=[]
            chooseMonth.forEach((val)=>{
                let key='month'+val
                chooseData.push(e.monthData[0][key])
                title=val.toString()+"、"
                console.log()
            })
            lineData.push({
                data:chooseData,
                name:e.brandName,
                type: 'line'
            })
        })
        // title.slice(0,str.length-1)+"月折线图"
        chooseMonth.forEach((e)=>{
            xAxis.push(e+"月")
        })
        canvas=echarts1.setOption({
            title: {
                text: title,
                left:'left'
            },
            tooltip: {
                trigger: 'axis'
            },
            legend: {
                right:20,
                orient: 'vertical',
            },
            grid: {
                left: '3%',
                right: '4%',
                bottom: '3%',
                containLabel: true
            },
            toolbox: {
                feature: {
                    saveAsImage: {}
                }
            },
            xAxis: {
                type: 'category',
                data: xAxis
            },
            yAxis: {
                type: 'value'
            },
            series: lineData
        });
    }
    if(echaetsType=="pie"){
        let pieData=[]
        brandData.forEach((e)=>{
            let total=0
                chooseMonth.forEach((val)=>{
                let key='month'+val
                total+=e.monthData[0][key]
            })
            pieData.push({
                value:total,
                name:e.brandName
            })
        })
        canvas=echarts1.setOption({
            title: {
                text: '',
                left: 'left'
            },
            tooltip: {
                trigger: 'item'
            },
            legend: {
                orient: 'vertical',
                left: 'right'
            },
            series: [
                {
                    name: '销量',
                    type: 'pie',
                    radius: '50%',
                    data: pieData,
                    emphasis: {
                        itemStyle: {
                            shadowBlur: 10,
                            shadowOffsetX: 0,
                            shadowColor: 'rgba(0, 0, 0, 0.5)'
                        }
                    }
                }
            ]
        })
    }

    if(echaetsType=="bar"){
        let barData=[]
        brandData.forEach((e)=>{
            let chooseData=[]
            chooseMonth.forEach((val)=>{
                let key='month'+val
                chooseData.push(e.monthData[0][key])
            })
            barData.push({
                data:chooseData,
                name:e.brandName,
                type: 'bar'
            })
        })
        console.log(barData)
        chooseMonth.forEach((e)=>{
            xAxis.push(e+"月")
        })
        canvas=echarts1.setOption({
            xAxis: {
                type: 'category',
                data: xAxis
            },
            yAxis: {
                type: 'value'
            },
            tooltip: {
                trigger: 'axis'
            },
            legend: {
                orient: 'vertical',
                left: 'right'
            },
            series: barData
        })
    }

return canvas
}
export default getCanvas
