                    },
                },
                remark:""
            },
        }
    }),
    mutations: { },
    actions: {},
    getters: {  }
}
const moduleHeader = {
    state: () => ({
        name:"header data"
    }),
    mutations: {  },
    actions: {  },
    getters: {  }
}

const moduleBody = {
    state: () => ({
        name:"body data"
    }),
    mutations: {  },
    actions: {  }
}

const store = new Vuex.Store({
    modules: {
        aside: moduleAside,
        header: moduleHeader,
        body:moduleBody
    }
})
export default store
