import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)
const moduleAside={
    state:() =>{
        name:"aside data"
}
}

const store = new Vuex.Store({
    state: {
        count: 0,
        name:'导航一百'
    },
    mutations: {
        increment (state) {
            state.count++
        }
    }
})
export default store
