import * as echarts from'echarts'
const  echarts1=echarts.init(this.$refs.lineChart)


var getCanvas=function (lineData) {
    return echarts1.setOption({
        title: {
            text: '近半年数据对比'
        },
        tooltip: {
            trigger: 'axis'
        },
        legend: {
        },
        grid: {
            left: '3%',
            right: '4%',
            bottom: '3%',
            containLabel: true
        },
        toolbox: {
            feature: {
                saveAsImage: {}
            }
        },
        xAxis: {
            type: 'category',
            data: [ '五月', '六月','七月', '八月', '九月', '十月', ]
        },
        yAxis: {
            type: 'value'
        },
        series: lineData

    });
}
export default getCanvas
